mapper.update:
update board
set
board_name=#{board_name},
boardtype=#{boardtype},
course_id=#{course_id}
where id=#{id}

mapper.select:
SELECT id, board_name, boardtype, course_id
FROM BOARD
WHERE course_id = #{course_id}

SELECT ID, BOARD_NAME, BOARDTYPE, COURSE_ID
FROM BOARD
WHERE COURSE_ID=#{course_id} AND BOARDTYPE=#{boardtype};

SELECT id, board_name,
boardtype, course_id
FROM BOARD
WHERE course_id = #{course_id}
AND
board_name = #{board_name}

SELECT id, board_name, boardtype, course_id
FROM board
WHERE course_id = #{course_id}
AND boardtype = #{boardtype}

select id, board_name
from board
where category = #{category} and course_id = #{courseid}
