mapper.update:
UPDATE schedule
SET start = #{start},
end = #{end},
title = #{title},
color = #{color}
WHERE id = #{id}

UPDATE schedule
SET start = #{start},
end = #{end}
WHERE id = #{id}

mapper.select:
SELECT id, start, end, title, color, group_id, course_id
FROM schedule
WHERE id = #{id}

SELECT id, start, end, title, color, group_id, course_id
FROM schedule
WHERE course_id = #{course_id}
AND group_id = #{group_id}
